#if defined USE_CAN_BUFFER
/*
function: Adds the SWC to Tx CAN buffer
input: the SWC command to send
output: none
*/
void SendSWC_Command_CAN1(uint8_t command) {
	CanTxMsgTypeDef canMessage;
	memset(&canMessage, 0, sizeof(canMessage));
	canMessage.ExtId = SWC_ID;    // Set extended ID for transmit message
	canMessage.IDE = CAN_ID_EXT;
	canMessage.RTR = CAN_RTR_DATA; // data
	canMessage.DLC = 4; // data length
	
	canMessage.Data[0] = command;		
	AddTxBuffer1(&canMessage);	
}
#endif // USE_CAN_BUFFER

#if defined FORD

uint8_t SWC_Data;

void ServiceSWC(uint8_t *data) {
	SWC_Data = data[0];
}

uint8_t GetSWC_ButtonStatus(void) {
	return SWC_Data & 0x0F;
}

#endif // FORD

